import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Button from 'react-bootstrap/Button';


const edit = (el) => {
    console.log(el);
};

let id = 0;

export default class MyComponent extends Component {

    state = {
        data: [
            { id: 1, title: 'Conan the Barbarian1', year: '1981' },
            { id: 2, title: 'Conan the Barbarian2', year: '1982' },
            { id: 3, title: 'Conan the Barbarian3', year: '1983' },
            { id: 4, title: 'Conan the Barbarian4', year: '1984' }
        ],
        columns: [
            {
                name: 'Title',
                selector: 'title',
                sortable: true,
            },
            {
                name: 'Year',
                selector: 'year',
                sortable: true,
                right: true,
            },
            {
                name: 'buttons',
                id: 'edit',
                cell: ({ year, id }) =>
                    <>
                        <Button className='mr-2' onClick={() => edit(year)}>edit</Button>
                        <Button onClick={() => this.del(id)}>del</Button>
                    </>
            }
        ]
    };

    add = () => {
        const newItem = { id: 5, title: 'Conan the Barbarian5', year: '1985' };
        this.setState(({ data }) => {
            const newData = [
                ...data,
                newItem
            ];
            return {
                data: newData
            }
        });
    };

    createItem = (item) => {
        return {
            id: 5,
            title: 'Conan the Barbarian5',
            year: '1985'
        }
    };

    del = (id) => {
        this.setState(({ data }) => {
            const idx = data.findIndex((el) => el.id === id);

            const newArray = [
                ...data.slice(0, idx),
                ...data.slice(idx + 1)
            ];

            return {
                data: newArray
            };
        });
    };

    render() {
        const { data, columns } = this.state;

        return (
            <>
                <Button className='mr-2' onClick={this.add}>add</Button>
                <DataTable
                    columns={columns}
                    data={data}
                />
            </>
        )
    }
};
