import React from 'react';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import './modal-row-add.css';
import { colourOptions } from '../data/data';
import Select from "react-select";




export default class ModalRowAdd extends React.Component {


    state = {
        id: '',
        fio: '',
        telefon_number: '',
        department: '',
        position: '',
        isClearable: true,
        isDisabled: false,
        isLoading: false,
        isRtl: false,
        isSearchable: true,
    };


    componentDidUpdate() {
        if (this.props.item.id !== this.state.id) {
            this.setState({
                id: this.props.item.id,
                fio: this.props.item.fio,
                telefon_number: this.props.item.telefon_number,
                department: this.props.item.department,
                position: this.props.item.position
            });
        }
    };

    toggleClearable = () =>
        this.setState(state => ({ isClearable: !state.isClearable }));
    toggleDisabled = () =>
        this.setState(state => ({ isDisabled: !state.isDisabled }));
    toggleLoading = () =>
        this.setState(state => ({ isLoading: !state.isLoading }));
    toggleRtl = () => this.setState(state => ({ isRtl: !state.isRtl }));
    toggleSearchable = () =>
        this.setState(state => ({ isSearchable: !state.isSearchable }));

    handleSubmit = (e) => {
        e.preventDefault();
        if (this.state.id !== undefined) {
            const newItem = {
                id: this.state.id,
                fio: this.state.fio.trim(),
                telefon_number: this.state.telefon_number.trim(),
                department: this.state.department,
                position: this.state.position,
            }

            this.props.onAdd(newItem);
        }
        this.onhandleClose();
    };

    handleChange = (e) => {
        console.log(e.target.name);
        this.setState({
            [e.target.name]: e.target.value
        });
    };


    onhandleClose = () => {
        this.props.onClose();
        this.setState({
            id: '',
            fio: '',
            telefon_number: '',
            department: '',
            position: ''
        });
    };

    onHideNull = () => {

    };


    render() {

        const { show } = this.props;
        const {
            id,
            fio,
            telefon_number,
            department,
            position,
            isClearable,
            isSearchable,
            isDisabled,
            isLoading,
            isRtl
        } = this.state;

        return (
            <Modal show={show} size="lg" onHide={this.onHideNull} >
                <form onSubmit={this.handleSubmit}>
                    <Modal.Body>
                        <Container>
                            <Card style={{ height: '35rem' }}>
                                <Card.Img style={{ height: '150px', width: '150px' }} variant="top" src="https://www.imgonline.com.ua/examples/bee-on-daisy.jpg" />
                                <Card.Body>
                                    <Card.Title>
                                        Карточка сотрудника
                                    </Card.Title>
                                    <Card.Text>
                                        <Form.Label>Отдел</Form.Label>

                                        <Select
                                            className="basic-single"
                                            classNamePrefix="select"
                                            defaultValue={department}
                                            isDisabled={isDisabled}
                                            isLoading={isLoading}
                                            isClearable={isClearable}
                                            isRtl={isRtl}
                                            isSearchable={isSearchable}
                                            name="department"
                                            options={colourOptions}
                                            onChange={this.handleChange}
                                        />

                                        {/* <FormControl
                                            type='text'
                                            name='department'
                                            value={department}
                                            onChange={this.handleChange}
                                        /> */}

                                        <FormControl
                                            type='text'
                                            hidden
                                            disabled
                                            name='id'
                                            value={id}
                                            onChange={this.handleChange}
                                        />
                                        <Form.Label>ФИО</Form.Label>
                                        <FormControl
                                            type='text'
                                            name='fio'
                                            value={fio}
                                            onChange={this.handleChange}
                                        />
                                        <Form.Label>Должность</Form.Label>
                                        <FormControl
                                            type='text'
                                            name='position'
                                            value={position}
                                            onChange={this.handleChange}
                                        />
                                        <Form.Label>Телефон</Form.Label>
                                        <FormControl
                                            type='text'
                                            name='telefon_number'
                                            value={telefon_number}
                                            onChange={this.handleChange}
                                        />
                                    </Card.Text>
                                    <Form.Group >
                                        <Button className='mr-2' variant="secondary" onClick={this.onhandleClose}>
                                            Закрыть
                                    </Button>
                                        <Button className='mr-2' type='submit' variant="primary">
                                            Сохранить
                                    </Button>
                                    </Form.Group>
                                </Card.Body>
                            </Card>
                        </Container>
                    </Modal.Body>
                </form>
            </ Modal >
        );
    }
};
