import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';





export default class ModalRowAdd extends React.Component {


    state = {
        id: '',
        fio: '',
        telefon_number: '',
        department: '',
        position: ''
    };

    componentDidUpdate() {
        if (this.props.item.id !== this.state.id) {
            this.setState({
                id: this.props.item.id,
                fio: this.props.item.fio,
                telefon_number: this.props.item.telefon_number,
                department: this.props.item.department,
                position: this.props.item.position
            });
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();
        if (this.state.id !== undefined) {
            const newItem = {
                id: '',
                fio: this.state.fio.trim(),
                telefon_number: this.state.telefon_number.trim(),
                department: this.state.department,
                position: this.state.position,
            }

            this.props.onAdd(newItem);
        }
        this.onhandleClose();
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };


    onhandleClose = () => {
        this.props.onClose();
        this.setState({
            id: '',
            fio: '',
            telefon_number: '',
            department: '',
            position: ''
        });
    };

    onHideNull = () => {

    };


    render() {

        const { show } = this.props;
        const { id, fio, telefon_number, department, position } = this.state;

        return (
            <Modal show={show} size="lg" onHide={this.onHideNull} >
                <form onSubmit={this.handleSubmit}>
                    <Modal.Body>
                        <Container>
                            <FormControl
                                type='text'
                                hidden
                                disabled
                                name='id'
                                value={id}
                                onChange={this.handleChange}
                            />
                            <Form.Group >
                                <Form.Label>Отдел</Form.Label>
                                <FormControl
                                    type='text'
                                    name='department'
                                    value={department}
                                    onChange={this.handleChange}
                                />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>ФИО</Form.Label>
                                <FormControl
                                    type='text'
                                    name='fio'
                                    value={fio}
                                    onChange={this.handleChange}
                                />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>Должность</Form.Label>
                                <FormControl
                                    type='text'
                                    name='position'
                                    value={position}
                                    onChange={this.handleChange}
                                />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>Телефон</Form.Label>
                                <FormControl
                                    type='text'
                                    name='telefon_number'
                                    value={telefon_number}
                                    onChange={this.handleChange}
                                />
                            </Form.Group>


                            <Button className='mr-2' variant="secondary" onClick={this.onhandleClose}>
                                Закрыть
                                    </Button>
                            <Button type='submit' variant="primary">
                                Сохранить
                            </Button>


                        </Container>
                    </Modal.Body>
                </form>
            </ Modal>
        );
    }
};
