import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Button from 'react-bootstrap/Button';
import FormControl from 'react-bootstrap/FormControl';
import ModalRowAdd from '../modal-row-add/modal-row-add';


let id_count = 5;

export default class MyComponent extends Component {

    state = {
        data: [
            { id: 1, title: 'Conan the Barbarian1', year: '1981' },
            { id: 2, title: 'Conan the Barbarian2', year: '1982' },
            { id: 3, title: 'Conan the Barbarian3', year: '1983' },
            { id: 4, title: 'Привет как дела', year: 'vvvv' }
        ],
        columns: [
            {
                name: 'Title',
                selector: 'title',
                sortable: true,
            },
            {
                name: 'Year',
                selector: 'year',
                sortable: true,
                right: true,
            },
            {
                name: 'buttons',
                id: 'edit',
                cell: ({ id }) =>
                    <>
                        <Button className='mr-2' onClick={() => this.edit(id)}>edit</Button>
                        <Button onClick={() => this.del(id)}>del</Button>
                    </>
            },
        ],
        tern: '',
        show: false,
        itemModal: {}
    };

    openMod = () => {
        this.setState({
            show: true,
            itemModal: { id: '', title: '', year: '' }
        });
    };

    add = (item) => {
        const idx = this.state.data.findIndex((el) => el.id === item.id);

        if (idx === -1 && item.title !== '' && item.year !== '') {
            const newItem = this.createItem(item);
            this.setState(({ data }) => {
                const newData = [
                    ...data,
                    newItem
                ];
                return {
                    data: newData
                }
            });
        }
        else {
            this.setState(({ data }) => {
                const findItem = this.state.data.filter((el) => el.id === item.id);
                findItem[0].title = item.title;
                const newData = [
                    ...data
                ];
                return {
                    data: newData
                }
            });
        }
    };


    createItem = (item) => {
        return {
            id: id_count++,
            title: item.title,
            year: item.year
        }
    };

    del = (id) => {
        this.setState(({ data }) => {
            const idx = data.findIndex((el) => el.id === id);

            const newArray = [
                ...data.slice(0, idx),
                ...data.slice(idx + 1)
            ];

            return {
                data: newArray
            };
        });
    };

    search(items, tern) {
        if (tern.length === 0) {
            return items;
        }

        return items.filter((item) => {
            return item.title
                .toLowerCase()
                .indexOf(tern.toLowerCase()) > -1
        });
    };

    handleChange = (e) => {
        this.setState({
            tern: e.target.value
        });
    };

    edit = (id) => {
        const findItem = this.state.data.filter((item) => item.id === id);

        this.setState({
            itemModal: findItem[0],
            show: true
        });
    };

    close = () => {
        this.setState({
            show: false
        });
    };


    render() {
        const { data, columns, tern, show, itemModal } = this.state;
        const visibalItems = this.search(data, tern);


        return (
            <>
                <div className="d-flex">
                    <Button className='mr-2' onClick={this.openMod}>add</Button>
                    <FormControl
                        placeholder='поиск'
                        value={tern}
                        onChange={this.handleChange}
                    />
                </div>
                <DataTable
                    columns={columns}
                    data={visibalItems}
                    pagination
                    subHeader
                    persistTableHead
                />
                <ModalRowAdd show={show} item={itemModal} onClose={this.close} onAdd={this.add} />
            </>
        )
    }
};
