import React from 'react';
import DataTable, { createTheme } from 'react-data-table-component';

const data = [{ id: 1, title: 'Conan the Barbarian', year: '1982' }];
const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
];

class MyComponent extends Component {
    render() {
        return (
            <DataTable
                title="Arnold Movies"
                columns={columns}
                data={data}
            />
        )
    }
};

export default MyComponent;
