import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Button from 'react-bootstrap/Button';
import FormControl from 'react-bootstrap/FormControl';
import ModalRowAdd from '../modal-row-add/modal-row-add';
import './app.css';
import { BsFillTrashFill } from "react-icons/bs";
import { BsPencilSquare } from "react-icons/bs";

let id_count = 5;

export default class MyComponent extends Component {

    state = {
        data: [
            { id: 1, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 2, fio: 'Петров Максим Иванович', year: '103' },
            { id: 3, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 4, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 5, fio: 'Петров Максим Иванович', year: '103' },
            { id: 6, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 7, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 8, fio: 'Петров Максим Иванович', year: '103' },
            { id: 9, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 10, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 11, fio: 'Петров Максим Иванович', year: '103' },
            { id: 12, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 13, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 14, fio: 'Петров Максим Иванович', year: '103' },
            { id: 15, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 16, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 17, fio: 'Петров Максим Иванович', year: '103' },
            { id: 18, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 19, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 20, fio: 'Петров Максим Иванович', year: '103' },
            { id: 21, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 22, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 23, fio: 'Петров Максим Иванович', year: '103' },
            { id: 24, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 25, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 26, fio: 'Петров Максим Иванович', year: '103' },
            { id: 27, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 28, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 29, fio: 'Петров Максим Иванович', year: '103' },
            { id: 30, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 31, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 32, fio: 'Петров Максим Иванович', year: '103' },
            { id: 33, fio: 'Кузнецов Константин Васильевич', year: '789' },
            { id: 34, fio: 'Иванов Илья Сергееви', year: '001' },
            { id: 35, fio: 'Петров Максим Иванович', year: '103' },
            { id: 36, fio: 'Кузнецов Константин Васильевич', year: '789' },

        ],
        columns: [
            {
                name: 'ФИО',
                selector: 'fio',
                sortable: true,
                style: {
                    color: '#202124',
                    fontSize: '14px',
                    fontWeight: 500
                },
            },
            {
                name: 'Year',
                selector: 'year',
                sortable: true,
                right: true,
                width: '350px',
                style: {
                    color: '#202124',
                    fontSize: '14px',
                    fontWeight: 500
                },
            },
            {
                name: 'buttons',
                id: 'edit',
                width: '150px',
                cell: ({ id }) =>
                    <>

                        <Button className='mr-2' onClick={() => this.edit(id)}>
                            <BsPencilSquare />
                        </Button>
                        <Button onClick={() => this.del(id)}>
                            <BsFillTrashFill />
                        </Button>
                    </>
            },
        ],
        tern: '',
        show: false,
        itemModal: {}
    };

    openMod = () => {
        this.setState({
            show: true,
            itemModal: { id: '', fio: '', year: '' }
        });
    };

    add = (item) => {
        const idx = this.state.data.findIndex((el) => el.id === item.id);

        if (idx === -1 && item.fio !== '' && item.year !== '') {
            const newItem = this.createItem(item);
            this.setState(({ data }) => {
                const newData = [
                    newItem,
                    ...data
                ];
                return {
                    data: newData
                }
            });
        }
        else if (idx !== -1) {
            this.setState(({ data }) => {
                const findItem = this.state.data.filter((el) => el.id === item.id);
                if (findItem) {
                    findItem[0].fio = item.fio;
                    findItem[0].year = item.year;
                    const newData = [
                        ...data
                    ];
                    return {
                        data: newData
                    }
                }
            });
        }
    };


    createItem = (item) => {
        return {
            id: id_count++,
            fio: item.fio,
            year: item.year
        }
    };

    del = (id) => {
        this.setState(({ data }) => {
            const idx = data.findIndex((el) => el.id === id);

            const newArray = [
                ...data.slice(0, idx),
                ...data.slice(idx + 1)
            ];

            return {
                data: newArray
            };
        });
    };

    search(items, tern) {

        if (tern.length === 0) {
            return items;
        }

        const findTitle = items.filter((item) => {
            return item.fio
                .toLowerCase()
                .indexOf(tern.toLowerCase().trim()) > -1;
        });

        const findYear = items.filter((item) => {
            return item.year
                .toLowerCase()
                .indexOf(tern.toLowerCase().trim()) > -1;
        });

        const newArrData = [...new Set([...findTitle, ...findYear])];

        return newArrData;
    };

    handleChange = (e) => {

        this.setState({
            tern: e.target.value
        });
    };

    edit = (id) => {
        const findItem = this.state.data.filter((item) => item.id === id);

        this.setState({
            itemModal: findItem[0],
            show: true
        });
    };

    close = () => {
        this.setState({
            show: false
        });
    };



    render() {
        const { data, columns, tern, show, itemModal } = this.state;
        const visibalItems = this.search(data, tern);

        const DataTableDB = () => {
            return (
                <DataTable
                    columns={columns}
                    data={visibalItems}
                    pagination
                    paginationPerPage={15}
                    noHeader
                />);
        };

        return (
            <div>
                <div className="d-flex mb-3 ml-2">
                    <div class="mr-auto p-2 ">
                        <Button className='mr-auto p-2' onClick={this.openMod}>Создать</Button>
                    </div>
                    <div class="p-2 form-contrl">
                        <FormControl
                            placeholder='поиск'
                            value={tern}
                            onChange={this.handleChange}
                        />
                    </div>
                </div>
                <DataTableDB />
                <ModalRowAdd show={show} item={itemModal} onClose={this.close} onAdd={this.add} />
            </div>
        )
    }
};
