import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';





export default class ModalRowAdd extends React.Component {

    state = {
        id: '',
        title: '',
        year: ''
    };


    // onhandleAdd = (e) => {
    //     e.preventDefault();
    //     this.props.onAddItemForm(this.state);
    //     this.onhandleClose();
    // };

    // handleChange = (e) => {
    //     this.setState({
    //         [e.target.name]: e.target.value
    //     });
    // };


    onhandleClose = (e) => {
        // this.props.onhandleClose();
        this.setState({
            id: '',
            title: '',
            year: ''
        });
    };

    // onHideNull = () => {
    //     let i = 0;
    // };

    render() {
        const { show } = this.props;
        console.log(this.props);
        return (
            <Modal show={show} size="lg" onHide={this.onHideNull} >
                <Modal.Body>
                    <Container>
                        <Row className='modal-row'>
                            <Col>
                                <FormControl type='text'
                                    name='id'
                                    defaultValue={this.props.item.id}
                                    onChange={this.handleChange}
                                    placeholder="тема" />
                            </Col>
                            <Col>
                                <FormControl type='text'
                                    name='title'
                                    defaultValue={this.props.item.title}
                                    onChange={this.handleChange}
                                    placeholder="ответственный" />
                            </Col>
                            <Col>
                                <FormControl type='text'
                                    name='year'
                                    defaultValue={this.props.item.year}
                                    onChange={this.handleChange}
                                    placeholder="text" />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Button className='mr-2' variant="secondary" onClick={this.onhandleClose}>
                                    Закрыть
                                        </Button>
                                <Button variant="primary" onClick={this.onhandleAdd}>
                                    Сохранить
                                        </Button>
                            </Col>
                        </Row>
                    </Container>
                </Modal.Body>
            </ Modal>
        );
    }
};
