import React, { Component } from 'react';
import DataTable, { createTheme } from 'react-data-table-component';

const data = [
    { id: 1, title: 'Conan the Barbarian0', year: '1980' },
    { id: 1, title: 'Conan the Barbarian1', year: '1981' },
    { id: 1, title: 'Conan the Barbarian2', year: '1982' },
    { id: 1, title: 'Conan the Barbarian3', year: '1983' }
];
const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
    {
        name: 'buttons',
        id: 'edit',
        accessor: '[row identifier to be passed to button]',
        Cell: () => (<button onClick={this.editRow()}>Edit</button>)
    },
];

class MyComponent extends Component {
    render() {
        return (
            <DataTable
                columns={columns}
                data={data}
            />
        )
    }
};

export default MyComponent;
