import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';





export default class ModalRowAdd extends React.Component {


    state = {
        id: '',
        title: '',
        year: ''
    };

    componentDidUpdate() {
        if (this.props.item.id !== this.state.id) {
            this.setState({
                id: this.props.item.id,
                title: this.props.item.title,
                year: this.props.item.year
            });
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();
        if (this.state.id !== undefined) {
            const newItem = {
                id: '',
                title: this.state.title.trim(),
                year: this.state.year.trim()
            }

            this.props.onAdd(newItem);
        }
        this.onhandleClose();
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };


    onhandleClose = () => {
        this.props.onClose();
        this.setState({
            id: '',
            title: '',
            year: ''
        });
    };

    onHideNull = () => {

    };


    render() {

        const { show } = this.props;
        const { id, title, year } = this.state;

        return (
            <Modal show={show} size="lg" onHide={this.onHideNull} >
                <form onSubmit={this.handleSubmit}>
                    <Modal.Body>
                        <Container>
                            <Row className='modal-row'>
                                <Col>
                                    <FormControl type='text'
                                        name='id'
                                        value={id}
                                        onChange={this.handleChange}
                                    />
                                </Col>
                                <Col>
                                    <FormControl type='text'
                                        name='title'
                                        value={title}
                                        onChange={this.handleChange}
                                    />
                                </Col>
                                <Col>
                                    <FormControl type='text'
                                        name='year'
                                        value={year}
                                        onChange={this.handleChange}
                                    />
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <Button className='mr-2' variant="secondary" onClick={this.onhandleClose}>
                                        Закрыть
                                </Button>
                                    <Button type='submit' variant="primary">
                                        Сохранить
                                </Button>
                                </Col>
                            </Row>
                        </Container>
                    </Modal.Body>
                </form>
            </ Modal>
        );
    }
};
