import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Button from 'react-bootstrap/Button';

edit = (el) => {
    console.log(el);
};

const data = [
    { id: 1, title: 'Conan the Barbarian0', year: '1980' },
    { id: 2, title: 'Conan the Barbarian1', year: '1981' },
    { id: 3, title: 'Conan the Barbarian2', year: '1982' },
    { id: 4, title: 'Conan the Barbarian3', year: '1983' }
];
const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
    {
        name: 'buttons',
        id: 'edit',
        cell: ({ value }) => <Button onClick={this.edit(value)}>edit</Button>,
    },
];

export default class MyComponent extends Component {
    render() {
        return (
            <DataTable
                columns={columns}
                data={data}
            />
        )
    }
};
