import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Button from 'react-bootstrap/Button';


const edit = (el) => {
    console.log(el);
};

const del = (id) => {
    console.log(id);
};


const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
    {
        name: 'buttons',
        id: 'edit',
        cell: ({ year, id }) =>
            <>
                <Button className='mr-2' onClick={() => edit(year)}>edit</Button>
                <Button onClick={() => del(id)}>del</Button>
            </>
    }
];

export default class MyComponent extends Component {

    state = {
        data: [
            { id: 1, title: 'Conan the Barbarian0', year: '1980' },
            { id: 2, title: 'Conan the Barbarian1', year: '1981' },
            { id: 3, title: 'Conan the Barbarian2', year: '1982' },
            { id: 4, title: 'Conan the Barbarian3', year: '1983' }
        ]
    };

    add = () => {
        const newItem = { id: 5, title: 'Conan the Barbarian5', year: '1985' };
        this.setState(({ data }) => {
            const newData = [
                ...data,
                newItem
            ];
            return
        });
    };

    render() {
        const { data } = this.state;
        return (
            <>
                <Button className='mr-2' onClick={this.add}>add</Button>
                <DataTable
                    columns={columns}
                    data={data}
                />
            </>
        )
    }
};
