import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import Button from 'react-bootstrap/Button';
import FormControl from 'react-bootstrap/FormControl';
import ModalRowAdd from '../modal-row-add/modal-row-add';


const edit = (el) => {
    console.log(el);
};

let id_count = 5;

export default class MyComponent extends Component {

    state = {
        data: [
            { id: 1, title: 'Conan the Barbarian1', year: '1981' },
            { id: 2, title: 'Conan the Barbarian2', year: '1982' },
            { id: 3, title: 'Conan the Barbarian3', year: '1983' },
            { id: 4, title: 'Привет как дела', year: 'vvvv' }
        ],
        columns: [
            {
                name: 'Title',
                selector: 'title',
                sortable: true,
            },
            {
                name: 'Year',
                selector: 'year',
                sortable: true,
                right: true,
            },
            {
                name: 'buttons',
                id: 'edit',
                cell: ({ year, id }) =>
                    <>
                        <Button className='mr-2' onClick={() => edit(year)}>edit</Button>
                        <Button onClick={() => this.del(id)}>del</Button>
                    </>
            },
        ],
        tern: '',
        show: false
    };



    add = () => {
        const newItem = this.createItem('123', '098');
        this.setState(({ data }) => {
            const newData = [
                ...data,
                newItem
            ];
            return {
                data: newData
            }
        });
    };

    createItem = (title, year) => {
        return {
            id: id_count++,
            title: title,
            year: year
        }
    };

    del = (id) => {
        this.setState(({ data }) => {
            const idx = data.findIndex((el) => el.id === id);

            const newArray = [
                ...data.slice(0, idx),
                ...data.slice(idx + 1)
            ];

            return {
                data: newArray
            };
        });
    };

    search(items, tern) {
        if (tern.length === 0) {
            return items;
        }

        return items.filter((item) => {
            return item.title
                .toLowerCase()
                .indexOf(tern.toLowerCase()) > -1
        });
    }
    handleChange = (e) => {
        this.setState({
            tern: e.target.value
        });
    };
    render() {
        const { data, columns, tern, show } = this.state;
        const visibalItems = this.search(data, tern);

        return (
            <>
                <div className="d-flex">
                    <Button className='mr-2' onClick={this.add}>add</Button>
                    <FormControl
                        placeholder='поиск'
                        value={tern}
                        onChange={this.handleChange}
                    />
                </div>
                <DataTable
                    columns={columns}
                    data={visibalItems}
                    pagination
                    subHeader
                    persistTableHead
                />
                <ModalRowAdd show={show} />
            </>
        )
    }
};
