import React from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';





export default class ModalRowAdd extends React.Component {

    state = {
        task: '',
        sotrudnik: '',
        text: ''
    };


    onhandleAdd = (e) => {
        e.preventDefault();
        this.props.onAddItemForm(this.state);
        this.onhandleClose();
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };


    onhandleClose = (e) => {
        this.props.onhandleClose();
        this.setState({
            task: '',
            sotrudnik: '',
            text: ''
        });
    };

    onHideNull = () => {
        let i = 0;
    };

    render() {
        const { show } = this.props;

        return (
            <Modal show={show} size="lg" onHide={this.onHideNull} >
                <Modal.Body>
                    <Container>
                        <Row className='modal-row'>
                            <Col>
                                <FormControl type='text'
                                    name='task'
                                    defaultValue={this.props.item.task}
                                    onChange={this.handleChange}
                                    placeholder="тема" />
                            </Col>
                            <Col>
                                <FormControl type='text'
                                    name='sotrudnik'
                                    defaultValue={this.props.item.sotrudnik}
                                    onChange={this.handleChange}
                                    placeholder="ответственный" />
                            </Col>
                            <Col>
                                <FormControl type='text'
                                    name='text'
                                    defaultValue={this.props.item.text}
                                    onChange={this.handleChange}
                                    placeholder="text" />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Button className='mr-2' variant="secondary" onClick={this.onhandleClose}>
                                    Закрыть
                                        </Button>
                                <Button variant="primary" onClick={this.onhandleAdd}>
                                    Сохранить
                                        </Button>
                            </Col>
                        </Row>
                    </Container>
                </Modal.Body>
            </ Modal>
        );
    }
};
